	@Shared
	def simplePost = new Post( id: 1, 
		title: "Simple Post",
		content: "This is some content",
	    createDate: new Date(2),
	    updateDate: new Date(2))

	def "#post.title can be serialized to JSON #fixture"() {
		expect:
		asJson(post) == jsonFixture(fixture)
			
